(function() {
	tinymce.PluginManager.add('osm', function(editor, url) {
		
		// Register the command
		editor.addCommand('addOsm', function() {
			

			/* If you work with sessions in PHP and your client doesn't accept cookies you might need to carry
		       the session name and session ID in the request string (can look like this: "?PHPSESSID=88p0n70s9dsknra96qhuk6etm5").
		       These lines of code extract the necessary parameters and add them back to the filebrowser URL again. */

		    var cmsURL = window.location.toString();    // script URL - use an absolute path!
		    
		    cmsURL = cmsURL.substr(0, cmsURL.lastIndexOf('/') + 1) + "osm_popover.php";
		    
		    window.naveditor_activeEditor = editor;

		    editor.windowManager.open({
		        file : cmsURL,
		        title : 'Open Street Map einbinden',
		        width : 840,  // Your dimensions may differ - toy around with them!
		        height : 680,
		        resizable : "yes",
		        inline : "yes",  // This parameter only has an effect if you use the inlinepopups plugin!
		        close_previous : "no",
	            onclose: function(){
	                
	            }
		    }, {
		        
		    });


		});

		// Register example button
		editor.addButton('osm', {
			title : 'OSM Einfuegen',
			cmd : 'addOsm',
			icon : 'i-code',
			stateSelector : 'iframe'
		});

	});
})();